import React,{useState} from 'react';
import { StyleSheet, Text,View,FlatList} from 'react-native';
import {Picker} from "@react-native-community/picker";

export default function StartScreen({navigation}) {

    const [value, setValue] = useState('')
    const [listValue,setListValue] = useState([])
    console.log(listValue,'fdsf')
    navigation.setOptions({
        headerTitle: "Выбор упражнений",
      })
  return (
    <View style={styles.container}>
        <Text>sdfsd</Text>
        <Picker
          selectedValue={value} 
          onValueChange={(itemValue)=>{
              console.log(itemValue)
            setValue(itemValue)
            setListValue(...listValue,itemValue)
          }}>          
       <Picker.Item label="Выберите упражнения" value="Выберите упражнения" />
         {["отжимания", "подтягивания"].map((tr)=> {return (<Picker.Item label={tr} value={tr} />)})}
       </Picker> 
       <FlatList
        data={listValue}
        renderItem={({item}) => <Text style={styles.item}>{item}</Text>}
        />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title:{
    paddingBottom: 100
  }
});
