import React,{useState,useEffect} from 'react';
import { StyleSheet, Text,ScrollView,FlatList} from 'react-native';
import {Picker} from "@react-native-community/picker";
import { observer } from "mobx-react-lite"
import {useStore} from '../../components/provider'

const  StartScreen =  observer(({navigation}) => {



    useEffect(()=>{
        navigation.setOptions({
            headerTitle: "Выбор упражнений",
          })
    },[navigation])
    const {workout}  = useStore()

    const [value, setValue] = useState('')



  return (
    <ScrollView style={styles.container}>
        <Picker
          selectedValue={value} 
          onValueChange={(itemValue,d)=>{

            workout.onSelectWorkout(itemValue)
          }}>          
          <Picker.Item label={'Выбкрите упражнение'} value={''} key={''} />
         {workout.listWorkout.map((item,index)=> {return (<Picker.Item label={item} value={item} key={item} />)})}
       </Picker> 
       <FlatList
        data={workout.listSelectWorkout}
        renderItem={({item}) => <Text key={item.id} onPress={()=>workout.onRemoveSelectWorkout(item.id)}  style={styles.item}>{item.name}</Text>}
        />
    </ScrollView>
  );
})

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',

  },
  item: {
    padding: 10,
    fontSize: 18,
    height: 44,
  },
  title:{
    paddingBottom: 100
  }
});

export default   StartScreen
