import React,{useState} from 'react';
import { StyleSheet, Text,ScrollView} from 'react-native';
import { TextInput } from 'react-native-paper';
import {Picker} from "@react-native-community/picker";
import Specifications from '../../stores/specifications'
import DateTimePicker from '@react-native-community/datetimepicker';


export default function InitInfoScreen({navigation}) {
  const [text, setText] = React.useState('');
  const a =  new Specifications()
  const [date, setDate] = useState(new Date(1598051730000));
  const [mode, setMode] = useState('date');
  return (
    <ScrollView style={styles.container}>
    <TextInput
      label="Имя"
      value={text}
      onChangeText={text => setText(text)}
    />
     <DateTimePicker
          testID="dateTimePicker"
          value={date}
          mode={mode}
          is24Hour={true}
          display="default"

        />
      <Picker 
      selectedValue={a.formSpecification.height}
      onValueChange={(itemValue)=>{
        a.handleChange('height',itemValue)
      }}>
      <Picker.Item label="Выберите Рост" value="" />
          {a.listHeight.map((b)=>{
          return (
            <Picker.Item label={`${b} см`} value={b} />
          )
        })}
       </Picker>
       <Picker>
       <Picker.Item label="Выберите вес" value="" />
          {a.listWeight.map((w)=><Picker.Item label={`${w} кг`} value={w} />)}
       </Picker>
    </ScrollView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',

  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
  title:{
    paddingBottom: 100
  }
});
