import * as React from 'react';
import { StyleSheet, Text, ScrollView, TouchableOpacity, Button,View,Modal,Pressable } from 'react-native';

const ModalContainer = (props) => {
  const [modalVisible, setModalVisible] = React.useState(false);


  return (
    <Modal
    animationType="slide"
    transparent={true}
    visible={modalVisible}
    onRequestClose={() => {
      Alert.alert("Modal has been closed.");

    }}
  >
        <View style={styles.centeredView}>
      <View style={styles.modalView}>
        <Text style={styles.modalText}>{props.title}</Text>
        {props.children}      
        <Pressable
          style={[styles.button, styles.buttonClose]}
          onPress={() => setModalVisible(!modalVisible)}
        >
          <Text style={styles.textStyle}>Hide Modal</Text>

        </Pressable>
      </View>
    </View>
  </Modal>
  );
};


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',

    },
    containerItemList: {
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "row",
    },
    item: {
        padding: 10,
        fontSize: 18,
        height: 44,
    },
    title: {
        paddingBottom: 100
    },
    modalView: {
        margin: 20,
        marginTop: 300,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
      },
      button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
      },
      buttonOpen: {
        backgroundColor: "#F194FF",
      },
      buttonClose: {
        backgroundColor: "#2196F3",
      },
      textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
      },
      modalText: {
        marginBottom: 15,
        textAlign: "center"
      }
});

export default ModalContainer;
