import { makeAutoObservable } from "mobx"

class Workout {
  listWorkout = ["отжимания", "подтягивания"]  
  listSelectWorkout = [{
      name: '',
      limit: null,
      id: null,
      leftToDo: null,
      done: null,
  }]
  constructor() {
    makeAutoObservable(this)
}

    onSelectWorkout = (id,name) =>{

        const newItemListWorkout =  {name,
            id,
            limit: null,
            leftToDo: null,
            done: null,

        }
        this.listSelectWorkout = [...this.listSelectWorkout, newItemListWorkout]
    }
    onRemoveSelectWorkout = (id) =>{
        this.listSelectWorkout =  this.listSelectWorkout.filter(({idx})=>id !== idx)

    }

}

export default Workout
