import React, { useState, useLayoutEffect } from 'react';
import { StyleSheet, Text, ScrollView, TouchableOpacity, Button,View,Modal,Pressable } from 'react-native';
import { Picker } from "@react-native-community/picker";
import { observer } from "mobx-react-lite"
import { useStore } from '../../components/provider'
import { List } from 'react-native-paper';

const StartScreen = observer(({ navigation }) => {
    const [modalVisible, setModalVisible] = useState(true);

    useLayoutEffect(() => {
        navigation.setOptions({
            headerTitle: "Выбор упражнений",
            headerRight: () => (
                <Button
                    onPress={() => navigation.navigate('StartPage')}
                    title="начать"
                    color="red"
                />
            ),
            headerBackTitle: 'Назад'
        })
    }, [navigation])
    const { workout } = useStore()

    const [value, setValue] = useState('')


    return (
        <>
        <ScrollView style={styles.container}>

                 <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");

        }}
      >
            <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Hello World!</Text>
            <Pressable
              style={[styles.button, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </Pressable>
          </View>
        </View>
      </Modal>
            <Picker
                selectedValue={value}
                onValueChange={(itemValue) => {
                    setValue(itemValue)
                    if (itemValue !== value) {
                        workout.onSelectWorkout(itemValue)
                    }
                }}>
                <Picker.Item label={'Выберите упражнение'} value={''} key={''} />
                {workout.listWorkout.map((item) => { return (<Picker.Item label={item} value={item} key={item} />) })}
            </Picker>
            <List.Section>
    <List.Subheader>Cписок упражнений</List.Subheader>
    {workout.listSelectWorkout.map((item)=>
 <List.Item title={item.name} 
 onPress={() => console.log('232')}
 right={() => <TouchableOpacity onPress={() => workout.onRemoveSelectWorkout(item.id)} >
     <List.Icon 

    icon="delete"  /></TouchableOpacity>

} />
    )
   }
  </List.Section>
            {/* <FlatList
                data={workout.listSelectWorkout}
                renderItem={({ item }) =>
                    <View style={styles.containerItemList}>
                        <Text key={item.id} style={styles.item}>{item.name}</Text>
                        <AntDesign onPress={() => workout.onRemoveSelectWorkout(item.id)} name="delete" size={24} color="black" />
                    </View>
                }
            /> */}
        </ScrollView>
        </>
    );
})

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',

    },
    containerItemList: {
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "row",
    },
    item: {
        padding: 10,
        fontSize: 18,
        height: 44,
    },
    title: {
        paddingBottom: 100
    },
    modalView: {
        margin: 20,
        marginTop: 300,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
      },
      button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
      },
      buttonOpen: {
        backgroundColor: "#F194FF",
      },
      buttonClose: {
        backgroundColor: "#2196F3",
      },
      textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
      },
      modalText: {
        marginBottom: 15,
        textAlign: "center"
      }
});

export default StartScreen
